import './App.css';
import Header from './components/Header';
import Main from './components/Main';
import { useState, useEffect } from 'react';

function App() {
  const [records, postRecords] = useState([])
  const [userSearch, postSearch] = useState('')
  const matchingRecords = []

  useEffect(()=>{
    fetch('http://localhost:4001/transactions')
    .then(res=>res.json())
    .then(data => {
      for(let record of records){
        if(record.LowerCase().search(userSearch.toLowerCase()) > -1){
          matchingRecords.unshift(record)
        }
      }
      postRecords(matchingRecords)
      console.log(matchingRecords)
    })
  }, [])

  return (
    <div className="App">
      <Header />
      <form>
        <input type="text" placeholder="Type to filter records..." id="filter" onChange={
          e=>{
            postSearch(e.target.value) 
            console.log(userSearch)
          }
        } 
        />
      </form>
      <Main records={records} />
    </div>
  );
}

export default App;
