import { useState, useEffect } from 'react';

function ShowRecords(props){
    const [parentNode, setParentNode] = useState({})

    function RemoveRecord(parent){
        useEffect(()=>{
            fetch(`http://localhost:4001/transactions/${props.id}`,{
                method: 'DELETE'
            })
            .then(res=>res.json())
            .then(removedData=>console.log(removedData))
        }, [])
    }

    return(
        <>
        <tr>
            <td>{props.id}</td>
            <td>{props.description}</td>
            <td>{props.date}</td>
            <td>{props.amount}</td>
            <td>{props.category}</td>
            <td><button onClick={e=>RemoveRecord(e.target.parentNode.parentNode)}>Delete</button></td>
        </tr>
        </> 
    )
}

export default ShowRecords;
