import './style.css'
import ShowRecords from './Records';

function Main(){
    fetch('http://localhost:3000/transactions')
    .then(res=>res.json())
    .then(records => {
        return(
            <main>
                <form>
                    <input type="text" placeholder="Type to filter records..." id="filter" />
                </form>
                <table>
                    <tr>
                        <td>ID</td>
                        <td>Description</td>
                        <td>Date</td>
                        <td>Amount</td>
                        <td>Category</td>
                    </tr>
                    {records.map(record => <ShowRecords 
                                            id={record.id}
                                            description={record.description} 
                                            date={record.date} 
                                            amount={record.amount}
                                            category={record.category}
                                        />)}
                </table>
            </main>
        )
    })
}

export default Main;
