import './style.css'
import ShowRecords from './Records';
import { useState } from 'react';

function AddRecord(e){
    e.preventDefault()
    const [description, setDescription] = useState('')
    const [date, setDate] = useState('')
    const [amount, setAmount] = useState('')
    const [category, setCategory] = useState('')
    let form = e.target
    let newRecord = {
        description: description,
        date: date,
        amount: amount,
        category: category
    }
    fetch('http://localhost:4001/transactions',{
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(newRecord)
    })
    .then(res=>res.json())
    .then(newData => console.log(newData))
}

function Main(props){
    return(
        <main>
            <div className='new-record'>
                <form onSubmit={AddRecord}>
                    <input type="text" placeholder="Enter description" id="description"/>
                    <input type="date" id="date"/>
                    <input type="number" placeholder="Enter amount" id="amount"/>
                    <input type="text" placeholder="Enter category" id="category"/>
                    <input type="submit" value="Add transaction"/>
                </form>
            </div>
            <table>
                <tr>
                    <td>ID</td>
                    <td>Description</td>
                    <td>Date</td>
                    <td>Amount</td>
                    <td>Category</td>
                </tr>
                {props.records.map(record => <ShowRecords 
                                        id={record.id}
                                        description={record.description} 
                                        date={record.date} 
                                        amount={record.amount}
                                        category={record.category}
                                    />)}
            </table>
        </main>
    )
}

export default Main;
