import './App.css';
import Header from './components/Header';
import Main from './components/Main';
import { useState } from 'react';

function App() {
  const [records, postRecords] = useState([])
  fetch('http://localhost:4001/transactions')
  .then(res=>res.json())
  .then(data => postRecords(data))

  function FilterRecords(e){
    e.preventDefault()
    const [userSearch, postSearch] = useState('')
    const [matchingRecords, postMatchingRecords] = useState([])
    postSearch(e.target.filter.value)
    console.log(userSearch)
    for(let record of records){
      if(record.search(userSearch) > -1){
        postMatchingRecords(()=>{
          matchingRecords.unshift(record)
        })
        console.log(record)
      }
    }
  }

  return (
    <div className="App">
      <Header />
      <form>
        <input type="text" placeholder="Type to filter records..." id="filter" onChange={FilterRecords} />
      </form>
      <Main records={records} />
    </div>
  );
}

export default App;
