import './App.css';
import Header from './components/Header';
import Main from './components/Main';
import { useState } from 'react';

function filterRecords(e){
  const [records, postRecords] = useState([])
  let matchingRecords = []
  e.preventDefault()
  let search = e.target.filter.value
  fetch('http://localhost:4000/transactions')
  .then(res=>res.json())
  .then(data=>{
      for(let record of data){
          if(record.search(search)>-1){
              matchingRecords.unshift(record)
              postRecords(matchingRecords)
          }
      }
  })
}


function App() {
  const [records, postRecords] = useState([])
  fetch('http://localhost:4000/transactions')
  .then(res=>res.json())
  .then(data => postRecords(data))

  return (
    <div className="App">
      <Header />
      <form>
        <input type="text" placeholder="Type to filter records..." id="filter" onChange={filterRecords} />
      </form>
      <Main records={records} />
    </div>
  );
}

export default App;
