import './style.css'
import ShowRecords from './Records';

function addRecord(e){
    let form = e.target
    let newRecord = {
        description: form.description.value,
        date: form.date,
        amount: form.amount,
        category: form.category
    }

    fetch('http://localhost:3000/transactions',{
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(newRecord)
    })
    .then(res=>res.json())
    .then(newData => console.log(data))
}

function Main(props){
        return(
            <main>
                <form>
                    <input type="text" placeholder="Type to filter records..." id="filter" />
                </form>
                <div className='new-record'>
                    <form onSubmit={addRecord}>
                        <input type="text" placeholder="Enter description" id="description"/>
                        <input type="date" id="date"/>
                        <input type="number" placeholder="Enter amount" id="amount"/>
                        <input type="text" placeholder="Enter category" id="category"/>
                        <input type="submit" value="Add transaction"/>
                    </form>
                </div>
                <table>
                    <tr>
                        <td>ID</td>
                        <td>Description</td>
                        <td>Date</td>
                        <td>Amount</td>
                        <td>Category</td>
                    </tr>
                    {props.records.map(record => <ShowRecords 
                                            id={record.id}
                                            description={record.description} 
                                            date={record.date} 
                                            amount={record.amount}
                                            category={record.category}
                                        />)}
                </table>
            </main>
        )
}

export default Main;
