import './style.css'
import ShowRecords from './Records';
import { useState, useEffect } from 'react';

function Main(){
    const [description, setDescription] = useState('')
    const [date, setDate] = useState('')
    const [amount, setAmount] = useState('')
    const [category, setCategory] = useState('')
    const [records, postRecords] = useState([])
    const [userSearch, postSearch] = useState('')

    useEffect(()=>{
        fetch('http://localhost:4001/transactions')
        .then(res=>res.json())
        .then(data => postRecords(data))
    }, [])

    const matchingRecords = records.filter(record =>
        record.description.toLowerCase().search(userSearch.toLowerCase()) > -1
    );

    function AddRecord(e){
        e.preventDefault()
        let newRecord = {
            description: description,
            date: date,
            amount: amount,
            category: category
        }
        fetch('http://localhost:4001/transactions',{
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(newRecord)
        })
        .then(res=>res.json())
        .then(newData => console.log(newData))

        useEffect(()=>{
            fetch('http://localhost:4001/transactions')
            .then(res=>res.json())
            .then(data => postRecords(data))
        }, [])

    }

    return(
        <main>
            <form>
                <input type="text" placeholder="Type to filter records..." id="filter" onChange={
                e=>{
                    postSearch(e.target.value) 
                    console.log(userSearch)
                    console.log(matchingRecords)
                }
                } 
                />
            </form>
            <div className='new-record'>
                <form onSubmit={AddRecord}>
                    <input type="text" placeholder="Enter description" id="description" onChange={e=>setDescription(e.target.value)}/>
                    <input type="date" id="date" onChange={e=>setDate(e.target.value)}/>
                    <input type="number" placeholder="Enter amount" id="amount" onChange={e=>setAmount(e.target.value)}/>
                    <input type="text" placeholder="Enter category" id="category" onChange={e=>setCategory(e.target.value)}/>
                    <input type="submit" value="Add transaction"/>
                </form>
            </div>
            <table>
                <tr>
                    <td>ID</td>
                    <td>Description</td>
                    <td>Date</td>
                    <td>Amount</td>
                    <td>Category</td>
                </tr>
                {matchingRecords.map(record => <ShowRecords 
                                        id={record.id}
                                        description={record.description} 
                                        date={record.date} 
                                        amount={record.amount}
                                        category={record.category}
                                    />)}
            </table>
        </main>
    )
}

export default Main;
